import '../style/base.scss';
import '../style/Projects.scss'
import packageImg from '../img/package.JPG'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import  { faAngleRight, faAngleLeft } from '@fortawesome/free-solid-svg-icons'
import { useEffect, useRef, useMemo, createRef, useState } from 'react'

function Projects (props) {

    let state = 0
    let [currentId, setCurrentId] = useState(1)

    let leftButtonRef = useRef()
    let rightButtonRef = useRef()

    let projectsList = props.projectsList
    

    // const projectRefs = useMemo(() => {
    //     const refs = {}
    //     projectsList.forEach((project) => {
    //         refs[project.id] = createRef(null)
    //     })
    //     return refs
    // }, [projectsList])

    const handleLeftClick = () => {
        setCurrentId(currentId => currentId === 1? currentId : currentId - 1)
        projectsList[currentId - 1].state = 'active'
        
    }

    const handleRightClick = () => {
        setCurrentId(currentId => {
            let result = currentId === projectsList.length ? currentId : currentId + 1
            console.log(re);
        })
        projectsList[currentId - 1].state = 'active'


    }

    if (projectsList.length) {
        state = 1
    }

    

    useEffect(() => {
        leftButtonRef.current.addEventListener('click', handleLeftClick)
        rightButtonRef.current.addEventListener('click', handleRightClick)
    })

    const renderedProjects = useRef([])

    for (var project of projectsList) {
        renderedProjects.current.push(
            <div key={project.id} className={`project ${project.state}`}>
                <img className="project-img" src={project.img} alt={project.name}/>                    
                <div className="project-body">
                    <span className="project-heading">{project.name}</span>
                    <span className="project-description">{project.description}</span>
                </div>
            </div>
        )
    }


    console.log(renderedProjects);

    

    

    return (
        <div className='projects'>
            {state ? 
            <div>
                <div className={currentId === 1 ? 'left-button fade' : 'left-button'} ref={leftButtonRef}>
                    <FontAwesomeIcon icon={faAngleLeft}/>
                </div>
                <div className={currentId === projectsList.length ? 'right-button fade' : 'right-button'} ref={rightButtonRef}>
                    <FontAwesomeIcon icon={faAngleRight}/>
                </div>
                <div className='projects-wrap'>
                    {renderedProjects.current}
                </div>
            </div> : 
                <div className='img-wrap--empty'>
                    <img className='projects-img-empty' src={packageImg} alt='empty'></img>
                    <span className='projects-img-header'>No projects yet</span>
                    <span className='projects-img-body'>But there will be, soon :{')'}</span>
                </div>}
        </div>
    )
}

export default Projects
