import './style/Home.scss';
import './style/base.scss';
import { useRef, useEffect } from 'react';
export default function Home () {
    const appearHomeAnimationRef = useRef()

    const handleRevealHome = ( ) =>{ 
        var windowHeight = window.innerHeight;
        var elementTop =
            appearHomeAnimationRef.current.getBoundingClientRect().top;
        var elementBottom =
            appearHomeAnimationRef.current.getBoundingClientRect().bottom;
        var elementVisible = 150;
        if (
            elementTop < windowHeight - elementVisible ||
            - elementBottom < windowHeight - elementVisible
        ) {
            appearHomeAnimationRef.current.classList.add("active");
        } else {
            appearHomeAnimationRef.current.classList.remove("active");
        }
        console.log();
    }

    useEffect(() => {
        window.addEventListener('scroll', handleRevealHome)
    })


    return (
        <div className="home " >
            <div className="reveal" ref={appearHomeAnimationRef}>
                <span className="title">
                    Hi, my <br></br>
                    name is Vu
                </span>
                <div className="get-to-know-me btn"> Get to know me</div>
            </div>
        </div>
    )
}
