import './style/About.scss'
import profileImg from './img/Profile.png'
import text from './text/description.txt'
export default function About () {
    var fr=new FileReader();
    let description
    fr.onload=function(){
        description = fr.result;
        console.log(description);
    }
        
    fr.readAsText(text);

    // let description = fr.readAsText()
    let profile = {
        img: profileImg,
        description
    }
    return (
        <div className="about">
            <img className="profile-img" src={profile.img} alt="Profile"/>
            <p>
                {profile.description}
            </p>
        </div>
    )
}
