import './style/base.scss';
import './style/About.scss'
import profileImg from './img/Profile.png'
import { useRef, useEffect } from 'react'

export default function About () {
    let description = 'Hello my name is Vu. I\'m currently a high school student at VNU-HCM High school for the gifted. I love creating beautiful apps and websites.'
    let profile = {
        img: profileImg,
        description
    }

    const appearAnimationRef = useRef()

    const handleReveal = ( ) =>{ 
        let windowHeight = window.height
        let element = appearAnimationRef.current.getBoundingClientRect()
         
    }

    useEffect(() => {
        window.addEventListener('scroll', handleReveal)
    })

    return (
        <div className="about reveal" ref={appearAnimationRef}>
            <div className="profile">
                <div className="cover-img"></div>
                <img className="profile-img" src={profile.img} alt="Profile"/> 
            </div>
            <p>
                {profile.description}
            </p>
        </div>
    )
}
