import './style/Home.scss';
import './style/base.scss';
export default function Home () {
    const appearAnimationRef = useRef()

    const handleReveal = ( ) =>{ 
        var windowHeight = window.innerHeight;
        var elementTop = appearAnimationRef.current.getBoundingClientRect().top;
        var elementVisible = 150;
        if (elementTop < windowHeight - elementVisible) {
        appearAnimationRef.current.classList.add("active");
        } else {
        appearAnimationRef.current.classList.remove("active");
        }
    }

    useEffect(() => {
        window.addEventListener('scroll', handleReveal)
    })


    return (
        <div className="home reveal">
            <span className="title">
                Hi, my <br></br>
                name is Vu
            </span>
            <div className="get-to-know-me btn"> Get to know me</div>
        </div>
    )
}
