import '../style/Projects.scss'
import packageImg from '../img/package.JPG'
import pongImg from '../img/pong.png'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import  { faAngleRight, faAngleLeft } from '@fortawesome/free-solid-svg-icons'
import {useEffect, useRef, useMemo, createRef} from 'react'

function Projects () {

    let state = 0

    let leftButtonRef = useRef()
    let rightButtonRef = useRef()

    let projectsList = useMemo(()=> {
        [
            {
                name: 'Pong Game',
                img: pongImg,
                description: 'This is one of my first projects ever. Even though it\' very simple, it marks the first computer games I create ever',
                id: 1,
                state: 'before-active'
            },
            {
                name: 'Pong Game',
                img: pongImg,
                description: 'This is one of my first projects ever. Even though it\' very simple, it marks the first computer games I create ever',
                id: 2,
                state: 'active'
            },
            {
                name: 'Pong Game',
                img: pongImg,
                description: 'This is one of my first projects ever. Even though it\' very simple, it marks the first computer games I create ever',
                id: 3,
                state: 'after-active'
            }
        ]
    })

    let handleClick = () => {
        
    }

    useMemo(() => {
        const refs = {}
        projectsList.forEach((project) => {
            refs[project.id] = createRef(null)
        })
        return refs
    }, [projectsList])

    if (projectsList.length) {
        state = 1
    }

    useEffect(() => {
        leftButtonRef.current.addEventListener('click', handleClick)
    })

    let renderedProjects = []

    for (var project of projectsList) {
        renderedProjects.push(
            <div key={project.id} className={`project ${project.state}`}>
                <img className="project-img" src={project.img} alt={project.name}/>                    
                <div className="project-body">
                    <span className="project-heading">{project.name}</span>
                    <span className="project-description">{project.description}</span>
                </div>
            </div>
        )
    }

    

    

    return (
        <div className='projects'>
            {state ? 
            <div>
                <div className='left-button' ref={leftButtonRef}>
                    <FontAwesomeIcon icon={faAngleLeft}/>
                </div>
                <div className='right-button' ref={rightButtonRef}>
                    <FontAwesomeIcon icon={faAngleRight}/>
                </div>
                <div className='projects-wrap'>
                    {renderedProjects}
                </div>
            </div> : 
                <div className='img-wrap--empty'>
                    <img className='projects-img-empty' src={packageImg} alt='empty'></img>
                    <span className='projects-img-header'>No projects yet</span>
                    <span className='projects-img-body'>But there will be, soon :{')'}</span>
                </div>}
        </div>
    )
}

export default Projects
