import './Header.scss'; 
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMagnifyingGlass, faClose } from '@fortawesome/free-solid-svg-icons';


export default function Header() {

    let animationTime = 0.4

    let searchResult

    let openSearchBar = true

    const handleFind = () => {
        setTimeout(() => {
            openSearchBar = false
        }, animationTime) 
    }



    return (
        <div className="header">
            <span className='header__logo'>Vu Huy Bui</span>
            {openSearchBar && <div className='header__wrap'>
                <div className='header__navbar'>
                    <span className='header__nav'>Home</span>
                    <span className='header__nav'>About</span>
                    <span className='header__nav'>Projects</span>
                    <span className='header__nav'>Contacts</span>
                    
                </div>
                <div onClick={handleFind} className='header__search-icon'>
                    <FontAwesomeIcon icon={faMagnifyingGlass} />
                </div>
            </div>}
            {!openSearchBar && (
            <div className='header__search'>
                <input 
                placeholder='Search anything' 
                className='header__search-bar open' 
                onChange={e => { 
                    searchResult = e.target.value
                    console.log(searchResult);
                }} value={searchResult}
                ></input>
                <div  className='header__search-close open'>
                    <FontAwesomeIcon icon={faClose} />
                </div>
                <div className='header__search-line open'></div>
            </div>)}
        </div>
    )
}
