import './style/base.scss';
import './style/About.scss'
import profileImg from './img/Profile.png'
import { useImperativeHandle, forwardRef } from 'react'
function About (props, ref) {
    let description = 'Hello my name is Vu. I\'m currently a high school student at VNU-HCM High school for the gifted. I love creating beautiful apps and websites.'
    let profile = {
        img: profileImg,
        description
    }

    return (
        <div  className="about " >
            <div className="reveal">
                <div className="profile">
                    <div className="cover-img"></div>
                    <img className="profile-img" src={profile.img} alt="Profile"/> 
                </div>
                <p>
                    {profile.description}
                </p>
            </div>
        </div>
    )
}

export default forwardRef(About)
