import Header from './components/Header'
import Home from './components/Home'
import About from './components/About'
import Projects from './components/Projects'
import Contact from './components/Contact'

import pongImg from './img/pong.png'
import './style/App.scss'
import { useRef, useEffect } from 'react'

import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate ,
} from "react-router-dom";

function App() {
  const aboutRef = useRef()
  const homeRef = useRef()
  let collection = {
    homeRef,
    aboutRef
  }

  let projectsList = [
    {
        name: 'Pong Game',
        img: pongImg,
        description: 'This is one of my first projects ever. Even though it\' very simple, it marks the first computer games I create ever',
        id: 1,
        state: 'active'
    },
    {
        name: 'Pong Game',
        img: pongImg,
        description: 'This is one of my first projects ever. Even though it\' very simple, it marks the first computer games I create ever',
        id: 2,
        state: 'after-active'
    },
    {
        name: 'Pong Game',
        img: pongImg,
        description: 'This is one of my first projects ever. Even though it\' very simple, it marks the first computer games I create ever',
        id: 3,
        state: 'after-active'
    }
]



  const handleReveal = (e) => {
    for (let i in collection) {
      let windowHeight = window.innerHeight;
      let elementTop = collection[i].current.getBoundingClientRect().top;
      let elementVisible = 150;

      if (elementTop < windowHeight - elementVisible) {
        collection[i].current.classList.add("active");
      } else {
        collection[i].current.classList.remove("active");
      }
    }
  }
  useEffect(() =>{
    window.addEventListener('scroll', handleReveal)
  })


  return (
      <div>  
        {/* <Header />  */}
        <Router>
          <Routes>
            {/* Route to Home */}

            <Route exact path="/home" component={Home} />
            <Route path="/about" component={About} />
            <Route path="/projects" component={<Projects projectsList={projectsList}/>} />
            <Route path="/contact" component={<Contact/>} />
            <Route path="/" element={<Navigate replace to="/home" />} />
          </Routes>
        </Router>
      </div>
  );
}

export default App;
